using AcademyKit.Domain.Entities;
using PuppeteerSharp;

namespace AcademyKit.Infrastructure.Services
{
    public class DynamicImageGenerator : IDynamicImageGenerator
    {
        public async Task<byte[]> GenerateOgImage(
            string title,
            string author,
            string image,
            string logo,
            string company
        )
        {
            var browserFetcher = new BrowserFetcher();
            await browserFetcher.DownloadAsync();
            var options = new LaunchOptions
            {
                Headless = true,
                Args = new[] { "--no-sandbox", "--disable-setuid-sandbox" },
                DefaultViewport = new ViewPortOptions { Width = 1200, Height = 628 }
            };

            using var browser = await Puppeteer.LaunchAsync(options);
            using var page = await browser.NewPageAsync();

            var html = GetOpenGraphHtml(title, author, image, logo, company);

            await page.SetContentAsync(html);

            var screenshotStream = await page.ScreenshotStreamAsync(
                new ScreenshotOptions { Type = ScreenshotType.Png }
            );
            await browser.CloseAsync();
            var memoryStream = new MemoryStream();
            await screenshotStream.CopyToAsync(memoryStream);

            return memoryStream.ToArray();
        }

        public async Task<Stream> GenerateCertificateImage(
            CourseCertificate certificate,
            string fullName,
            IList<Signature> signatures,
            GeneralSetting company
        )
        {
            var browserFetcher = new BrowserFetcher();
            await browserFetcher.DownloadAsync();
            var options = new LaunchOptions
            {
                Headless = true,
                Args = new[] { "--no-sandbox", "--disable-setuid-sandbox" },
                DefaultViewport = new ViewPortOptions { Width = 906, Height = 591 }
            };

            using var browser = await Puppeteer.LaunchAsync(options);
            using var page = await browser.NewPageAsync();

            var training = certificate?.Title;
            var startDate = certificate?.EventStartDate.ToString("dd MMMM yyyy");
            var endDate = certificate?.EventEndDate.ToString("dd MMMM yyyy");
            var companyName = company.CompanyName;
            var companyLogo = company.LogoUrl;

            var html = GetCertificateHtml(
                companyLogo,
                companyName,
                fullName,
                training,
                startDate,
                endDate,
                signatures
            );

            await page.SetContentAsync(html);

            var screenshotStream = await page.ScreenshotStreamAsync(
                new ScreenshotOptions { Type = ScreenshotType.Png }
            );
            await browser.CloseAsync();
            var memoryStream = new MemoryStream();
            return screenshotStream;
        }

        private static string GetOpenGraphHtml(
            string title,
            string author,
            string image,
            string logo,
            string company
        )
        {
            StringBuilder html = new();

            html.AppendLine("<!DOCTYPE html>");
            html.AppendLine("<html lang=\"en\">");
            html.AppendLine("<head>");
            html.AppendLine("    <meta charset=\"UTF-8\">");
            html.AppendLine("    <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">");
            html.AppendLine(
                "    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">"
            );
            html.AppendLine("    <title>Document</title>");
            html.AppendLine("</head>");
            html.AppendLine("<body style=\"padding: 0px; margin: 0px; color: white;\">");

            html.AppendLine("<div style=\"height: 628px; width: 1200px; position: relative;\">");
            html.AppendLine(
                "    <div style=\"height: 628px; width: 1200px; background: linear-gradient(225deg, hsla(207, 79%, 68%, 1) 25%, hsla(175, 91%, 55%, 1) 100%); padding: 40px;\">"
            );
            html.AppendLine(
                $"        <img src=\"{image}\" style=\"position: absolute; height: 90px; width: 90px; top: 320px; left: 50px; border-radius: 100%;\" />"
            );
            html.AppendLine(
                $"        <img src=\"{logo}\" style=\"position: absolute; height: 150px; width: 150px; object-fit: contain; top: 40px; right: 40px;\" />"
            );
            html.AppendLine(
                "        <div style=\"position: absolute; left: 0px; height: 300px; width: 700px; top: 100px\">"
            );
            html.AppendLine(
                $"            <div style=\"padding: 55px; font-weight: bold; font-size: 50px; display: -webkit-box; -webkit-box-orient: vertical; -webkit-line-clamp: 2; line-height: 1.1; overflow: hidden; text-overflow: ellipsis; max-height: 170px;\">{title}</div>"
            );
            html.AppendLine("        </div>");
            html.AppendLine(
                $"        <div style=\"position: absolute; top: 310px; right: 350px; text-align: start; width: 600px; padding: 20px 55px; font-size: 35px; font-weight: bold; text-decoration: underline;\">{author}</div>"
            );
            html.AppendLine(
                $"        <div style=\"position: absolute; bottom: 40px; left: 550px; text-align: end; font-size: 35px; font-weight: bold; width: 600px; overflow: hidden; text-overflow: ellipsis;\">{company}</div>"
            );
            html.AppendLine("    </div>");
            html.AppendLine("</div>");
            html.AppendLine("</body>");

            return html.ToString();
        }

